import React, { Component } from 'react'
import  './index.css'
export default class A extends Component {
    state = {username:'tom'}
    render() {
        return (
            <div className ="parent">
                <h3>我是A组件</h3>
				<h4>我的用户名是:{this.stateusername}</h4>
                <B/>
            </div>
        )
    }
}

class B extends Component {
    render() {
        return (
            <div className="child">
               <h3>我是B组件</h3>
				<h4>我从A组件接收到的用户名是:</h4>
                <C/>
            </div>
        )
    }
}


class C extends Component {
    render() {
        return (
            <div>
                <h3>我是C组件</h3>
				<h4>我从A组件接收到的用户名是:{this.state.username}</h4>
            </div>
        )
    }
}
